import { FcDeleteDatabase } from "react-icons/fc";
import { useDispatch } from "react-redux";
import { remove } from "../redux/Slices/CartSlice";
import { toast } from "react-hot-toast";

const CartItem = ({ item, itemIndex }) => {
  const dispatch = useDispatch();

  const removeFromCart = () => {
    dispatch(remove(item.id));
    toast.success("Item Removed");
  };

  return (
    <div className="bg-white shadow-md p-4 mb-4 rounded-lg sm:flex sm:items-center sm:justify-between">
      <div className="flex items-center mb-4 sm:mb-0">
        <div className="w-60px h-60px sm:w-32 sm:h-32 mr-4">
          <img
            src={item.image}
            alt={item.title}
            className="w-100pz h-60px object-cover rounded"
          />
        </div>
        <div>
          <h1 className="text-lg font-semibold">{item.title}</h1>
          <p className="text-sm text-gray-500">{item.description}</p>
        </div>
      </div>
      <div className="flex items-center">
        <p className="text-lg font-semibold mr-4">{item.price}</p>
        <div onClick={removeFromCart} className="cursor-pointer">
          <FcDeleteDatabase />
        </div>
      </div>
    </div>
  );
};

export default CartItem;
