import { FcDeleteDatabase } from "react-icons/fc";
import { useDispatch } from "react-redux";
import { remove } from "../redux/Slices/CartSlice";
import { toast } from "react-hot-toast";

const CartItem = ({ item, itemIndex }) => {
  const dispatch = useDispatch();

  const removeFromCart = () => {
    dispatch(remove(item.id));
    toast.success("Item Removed");
  };

  // Function to truncate text to a specified number of words
  const truncateText = (text, maxWords) => {
    const words = text.split(" ");
    if (words.length <= maxWords) {
      return text;
    }
    return words.slice(0, maxWords).join(" ") + "...";
  };

  // Truncate the item.description to 50 words
  const truncatedDescription = truncateText(item.description, 10);

  return (
    <div className="bg-white shadow-md p-4  rounded-lg sm:flex sm:items-center sm:justify-between h-[350px] w-[350px]">
      <div className="flex items-center mb-[40px] sm:mb-0 ">
        <div className="w-[100px] h-[60px] sm:w-32 sm:h-32 mr-4">
          <img
            src={item.image}
            alt={item.title}
            className="w-[100px] h-[100px] object-cover rounded"
          />
        </div>
        <div className="h-[200px] w-[100px]">
          <h1 className="text-lg font-semibold">{item.title}</h1>
          <p className="text-gray-500 text-xs">{truncatedDescription}</p>
        </div>
      </div>
      <div className="flex items-center">
        <p className="text-lg font-semibold mr-4">{item.price}</p>
        <div onClick={removeFromCart} className="cursor-pointer">
          <FcDeleteDatabase />
        </div>
      </div>
    </div>
  );
};

export default CartItem;
